import React, { useState } from 'react';
import styled from 'styled-components';
import Footer from '../Footer';
import FilterItem from './FilterItem';

const FilterDiv = styled.div`
  position: absolute;
  width: 100%;
  transform: translateX(50%);

  .filter-item {
    transition: all 0.5s;
    &__name {
    }

    &__content {
      transition: all 0.5s;
      /* visibility: hidden; */
      /* transform: scaleY(0); */
    }
  }
`;

function Filters() {
  const [state, setstate] = useState({ price: false, color: false });

  const handleClick = (e) => {
    setstate((prev) => ({ ...prev, [e.target.id]: !prev[e.target.id] }));
  };
  return (
    <FilterDiv>
      <div className="filter-item">
        <div className="filter-item__name" id="price" onClick={handleClick}>
          cena
        </div>
        {state.price ? <FilterItem /> : ''}
      </div>
      <div className="filter-item">
        <div className="filter-item__name" id="color" onClick={handleClick}>
          cena
        </div>
        {state.color ? <FilterItem /> : ''}
      </div>
      <FilterItem />
    </FilterDiv>
  );
}

export default Filters;
