import React, { useState } from 'react';
import styled from 'styled-components';
import LightBgc from '../LightBgc';
import FilterItem from './FilterItem';

const FilterDiv = styled.div`
  position: absolute;
  width: 100%;
  transform: translateX(120%);
  transition: all 0.5s;

  .filter-item {
    &__name {
    }

    &__content {
      /* transition: all 0.5s; */
      /* visibility: hidden; */
      /* transform: scaleY(0); */
    }
  }
`;

function Filters() {
  const [state, setstate] = useState({ price: false, color: false });

  const handleClick = (e) => {
    setstate((prev) => ({ ...prev, [e.target.id]: !prev[e.target.id] }));
  };
  return (
    <FilterDiv className="sidebar">
      <LightBgc>
        <FilterItem show={state.color} handleClick={handleClick} />
        <FilterItem show={state.price} handleClick={handleClick} />
      </LightBgc>
    </FilterDiv>
  );
}

export default Filters;
