import React, { useState } from 'react';
import styled from 'styled-components';
import { colors } from '../styles';

const QuickViewContainer = styled.div`
  width: 90%;
  height: 300px;
  left: 5%;
  top: 50%;
  transform: translateY(-50%);
  /* margin: 0 auto; */
  position: fixed;
  border: 1px solid #000;
  background: rgba(${colors.lightgrey1});
  z-index: 1000;

  > div {
    height: 100%;
  }
`;

const Image = styled.div`
  height: 50%;
  background-image: ${(props) => `url(${props.img})`};
  background-position: center;
  background-size: contain;
  background-repeat: no-repeat;
`;
const ContentBox = styled.div`
  padding: 15px;
  > *:not(:last-child) {
    margin-bottom: 40px;
  }
`;
const Title = styled.h4``;
const Span = styled.span`
  display: inline-block;
  width: 100%;
`;
const ButtonsContainer = styled.div`
  display: flex;
`;
const Button = styled.div``;
const Close = styled.div`
  position: absolute;
  top: 5px;
  right: 5px;
`;

function QuickView() {
  const [state, setState] = useState();
  return (
    <QuickViewContainer>
      <Close>X</Close>
      <div>
        <Image img="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ7LKU2iEoaspX9tRXyvLWj_Jwp0hIMti2jhPO6NUEk9IU8B5ncs44RcSC-qDa8iGmDy0YknBrG&usqp=CAc"></Image>
        <ContentBox>
          <Title>t-shirt</Title>
          <Span>sklep: h&m</Span>
          <Span>rozmiary: 62, 68, 74</Span>
          <Span>kolory: czarny, różowy, mix</Span>
          <ButtonsContainer>
            <Button>zobacz</Button>
            <Button>sklep</Button>
          </ButtonsContainer>
        </ContentBox>
      </div>
    </QuickViewContainer>
  );
}

export default QuickView;
