import React from 'react';
import styled from 'styled-components';
import { boxShadow, colors } from '../../styles';
import ProductSmallCard from './ProductSmallCard';

const GridBox = styled.div`
  min-height: 300px;
  width: 100%;
  background-color: rgba(255, 255, 255, 0.3);
  backdrop-filter: blur(10px);
  margin: 20px 0;
  padding: 20px 0;
  border-radius: 6px;
  box-shadow: ${boxShadow('small')};

  display: flex;
  flex-direction: column;

  .products-header {
    h4.title {
    padding: 20px 10px;
    text-align: center;
    font-size: 22px;
  }

  .products-list {
    display: flex;
    flex-wrap: wrap;
  }

  /* button.load-more {
    width: 30%;
    max-width: 200px;
    margin: 25px auto 15px;
    padding: 5px 0;
    font-size: 18px;
  } */

  .more-products {
    div {
      display: flex;
      justify-content: flex-end;
      width: calc(100% - 20px);
      margin: 0 auto;

      i {
        padding: 5px;
        color: rgba(${colors.darkgrey2});
        cursor: pointer;
      }
    }
  }
  }
`;

function ProductGrid({ products, title, arrows, children }) {
  return (
    <GridBox className="product-grid">
      <div className="products-header">
      {children}
      <h4 className="products__title">{title}</h4>
      {arrows && <div className="products__arrows">
        <div>
          <i className="fa fa-chevron-left"></i>
          <i className="fa fa-chevron-right"></i>
        </div>
      </div>}
      </div>
      <div className="products-list">
        {products &&
          products.map((product) => (
            <ProductSmallCard data={product} key={product._id} />
          ))}
      </div>
      {!arrows && <div className="load-more"> load more</div>}
      {/* <button className="load-more">wiÄ™cej</button> */}
    </GridBox>
  );
}

export default ProductGrid;
