import React from 'react';
import styled from 'styled-components';
import { colors } from '../styles';
import Content from './Content';
import Date from './Date';
import Title from './Title';

const CardContainer = styled.div`
  > div {
    margin: 10px auto;
    padding: 10px;
    width: 95%;
    box-shadow: 0 0 8px -5px rgba(${colors.darkgrey4});
  }
`;

const Image = styled.div`
  background-image: ${(props) => `url(${props.img})`};
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  min-height: 200px;
  position: relative;
  border: 3px solid rgba(${colors.darkgrey4});
`;

const ContentContainer = styled.div``;

function BlogPostCardBig({ data }) {
  console.log(data);
  return (
    <CardContainer className="blog-post__big">
      <div className="card">
        <Image img={data.url}></Image>
        <ContentContainer>
          <Title title={data.title} />
          <Date date={data.date} />
          <Content text={data.content.slice(0, 300)} />
        </ContentContainer>
        <div className="btn">wiÄ™cej</div>
      </div>
    </CardContainer>
  );
}

export default BlogPostCardBig;
