import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { boxShadow, colors } from '../styles';

const Details = styled.div`
  padding: 0 30px;
  max-width: 90%;
  margin: 0 auto;

  > div {
    margin: 10px 0;
  }

  .price-boxes {
    form {
      display: flex;
      justify-content: space-around;
      div {
        display: flex;
        justify-content: center;
        align-items: center;

        label {
        }

        input[type='number'] {
          width: 20px;
          height: 20px;
          border: 2px solid rgba(${colors.lightgrey4});
          margin: 0 5px;
          box-shadow: ${boxShadow('xsmall')};
        }

        input[type='number']:focus {
          outline: 2px solid rgba(${colors.darkgrey3});
          box-shadow: ${boxShadow('medium')};
        }
      }
    }
  }

  .price-range {
    display: flex;
    justify-content: center;
  }
`;

function FilterDetails({ price, subcategory, color, size, shops }) {
  if (price) {
    return (
      <Details>
        <div className="price-boxes">
          <form>
            <div className="price__min">
              <label>min</label>
              <input type="number" name="min" id="min" />
            </div>
            <div className="price__max">
              <input type="number" name="max" id="max" />
              <label>max</label>
            </div>
          </form>
        </div>
        <div className="price-range">
          <form action="">
            <label htmlFor="">min</label>
            <input type="range" name="" id="" />
            <label htmlFor="">max</label>
          </form>
        </div>
      </Details>
    );
  } else if (subcategory) {
    return (
      <div className="subcategories">
        <ul>
          <li>
            <Link to="link">link</Link>
          </li>
          <li>
            <Link to="link">link</Link>
          </li>
          <li>
            <Link to="link">link</Link>
          </li>
          <li>
            <Link to="link">link</Link>
          </li>
        </ul>
      </div>
    );
  } else if (color) {
    return (
      <div className="colors">
        <form action="">
          <div className="color">
            <input type="checkbox" name="blue" id="blue" />
            <label>niebieski</label>
          </div>
          <div className="color">
            <input type="checkbox" name="blue" id="blue" />
            <label>niebieski</label>
          </div>
          <div className="color">
            <input type="checkbox" name="blue" id="blue" />
            <label>niebieski</label>
          </div>
          <div className="color">
            <input type="checkbox" name="blue" id="blue" />
            <label>niebieski</label>
          </div>
          <div className="color">
            <input type="checkbox" name="blue" id="blue" />
            <label>niebieski</label>
          </div>
        </form>
      </div>
    );
  } else if (size) {
    return (
      <div className="sizes">
        <form action="">
          <div className="size">
            <input type="checkbox" name="74" id="74" />
            <label>74</label>
          </div>
        </form>
      </div>
    );
  } else if (shops) {
    return (
      <div className="shops">
        <form action="">
          <div className="shop">
            <input type="checkbox" name="zalando" id="zalando" />
            <label>zalando</label>
          </div>
        </form>
      </div>
    );
  }
}

export default FilterDetails;
