import React from 'react';
import styled from 'styled-components';
import { colors } from '../styles';
import Content from './Content';
import Date from './Date';
import Title from './Title';

const CardContainer = styled.div`
  > div {
    margin: 10px auto;
    padding: 10px 20px;
    width: 95%;
    box-shadow: 0 0 8px -5px rgba(${colors.darkgrey4});
  }
`;

const Image = styled.div`
  background-image: ${(props) => `url(${props.img})`};
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  min-height: 200px;
  position: relative;
  border: 3px solid rgba(${colors.darkgrey4});
`;

const ContentContainer = styled.div`
  span {
    display: inline-block;

    &:first-of-type {
      width: 100%;
      margin: 10px 0;
    }

    &:nth-of-type(2) {
      padding: 3px;
      background-color: rgba(${colors.yellow});
    }
  }

  div {
    margin: 10px 0;

    p {
      font-size: 14px;
    }
  }
`;

function BlogPostCardBig({ data }) {
  console.log(data);
  return (
    <CardContainer className="blog-post__big">
      <div className="card">
        <Image img={data.url}></Image>
        <ContentContainer className="card__content">
          <Title title={data.title} />
          <Date date={data.date} />
          <Content text={data.content.slice(0, 300)} />
          <div className="content">
            <p>{data.content.slice(0, 300)}...</p>
          </div>
        </ContentContainer>
        <div className="btn">wiÄ™cej</div>
      </div>
    </CardContainer>
  );
}

export default BlogPostCardBig;
