import React from 'react';
import styled from 'styled-components';
import { colors } from '../styles';

const CardContainer = styled.div`
  > div {
    margin: 10px 0;
  }
`;

const Image = styled.div`
  background-image: ${(props) => `url(${props.img})`};
  background-position: center;
  background-size: contain;
  background-repeat: no-repeat;
  min-height: 200px;
  position: relative;

  span {
    display: inline-block;
    position: absolute;
    bottom: 0;
    left: 0;
    padding: 5px;
    background-color: rgba(${colors.yellow});
  }
`;

function BlogPostCardBig({ data }) {
  console.log(data);
  return (
    <CardContainer className="blog-post__big">
      <div className="card">
        <Image img={data.url}>
          <span>{data.date}</span>
        </Image>
        <div className="card__content">
          <span>{data.title}</span>
          <div className="content">{data.content.slice(0, 300)}...</div>
        </div>
        <div className="btn">wiÄ™cej</div>
      </div>
    </CardContainer>
  );
}

export default BlogPostCardBig;
