import React, { useState } from 'react';
import styled from 'styled-components';
import LightBgc from '../LightBgc';
import FilterItem from './FilterItem';

const FilterDiv = styled.div`
  position: absolute;
  width: 100%;
  transform: translateX(120%);
  transition: all .5
  /* display: none; */

  .filter-item {
    /* min-height: 100px; */
    /* transition: all 1s; */
    position: relative;
    overflow: hidden;
    &__name {
    }

    &__content {
      /* transition: all 0.5s; */
      /* visibility: hidden; */
      /* transform: scaleY(0); */
    }
  }
`;

function Filters() {
  const [state, setstate] = useState({ price: false, color: false });

  const handleClick = (e) => {
    setstate((prev) => ({ ...prev, [e.target.id]: !prev[e.target.id] }));
  };
  return (
    <FilterDiv className="sidebar">
        <LightBgc>
      <div className="filter-item">
        <div className="filter-item__name" id="price" onClick={handleClick}>
          cena
        </div>
        <FilterItem show={state.price} />
      </div>
      <div className="filter-item">
        <div className="filter-item__name" id="color" onClick={handleClick}>
          cena
        </div>
        <FilterItem show={state.color} />
      </div>
    </LightBgc>
    </FilterDiv>
  );
}

export default Filters;
