import React from 'react';
import styled from 'styled-components';
import { colors } from '../styles';

const CardContainer = styled.div`
  > div {
    margin: 10px auto;
    padding: 10px;
    width: 95%;
    background-color: rgba(${colors.lightgrey1}, 0.25);
    backdrop-filter: blur(3px);
    box-shadow: 0 0 8px -5px rgba(${colors.darkgrey4});
  }
`;

const Image = styled.div`
  background-image: ${(props) => `url(${props.img})`};
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  min-height: 200px;
  position: relative;
  border: 3px solid rgba(${colors.darkgrey4});
`;

const ContentContainer = styled.div``;

const H4 = styled.h4`
  font-size: 16px;
`;

const Span = styled.span`
  display: inline-block;
  padding: 3px;
  margin: 5px 0;
  font-size: 14px;
  background-color: rgba(${colors.yellow});
`;

const Content = styled.p`
  line-height: 1.4;
  font-size: 14px;
`;

const Button = styled.div`
  width: max-content;
  height: max-content;
  padding: 5px 10px;
  margin: 5px 0;
  background-color: rgba(${colors.yellow});
  /* border-radius: 20px; */
`;

function BlogPostCardMedium({ data }) {
  return (
    <CardsContainer className="blog-post__medium">
      {data.map((data) => (
        <div key={data.id} className="card">
          <Image img={data.url} className="card__image"></Image>
          <div className="card__content">
            <div className="title">{data.title}</div>
            <div className="date">{data.date}</div>
            <div className="content">{data.content.slice(0, 300)}...</div>
          </div>
        </div>
      ))}
    </CardsContainer>
  );
}

export default BlogPostCardMedium;
