import React, { useEffect } from 'react';
import styled from 'styled-components';

const Item = styled.div`
  &__name {

  }

  &__content {
    transition: all 0.5s;
  height: ${(props) => (props.show ? '100px' : '0')};
  }
`;

function FilterItem({ show, handleClick }) {
  return (
    <Item show={show} className="filter-item">
     <div className="filter-item__name" id="price" onClick={handleClick}>
      cena
    </div>
    <div className="filter-item__content">
      cena
    </div>
    </Item>

  );
}

export default FilterItem;
