import React from 'react';
import styled from 'styled-components';
import { colors } from '../styles';

const CardsContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const Card = styled.div`
  /* display: flex;
  justify-content: center;
  align-items: center; */
  /* flex-wrap: wrap; */
  display: grid;
  grid-template-columns: 1fr 3fr;
  grid-template-rows: 3fr 50px;

  .card__content {
    display: flex;
    flex-direction: column;
    align-self: stretch;

    margin: 10px 0;

    > div {
      margin: 3px 0;
    }
  }

  .btn {
    grid-column-start: 2;
    width: max-content;
    height: max-content;
    padding: 5px 10px;
    background-color: rgba(${colors.yellow});
    border-radius: 20px;
  }
`;

const Image = styled.div`
  background-image: ${(props) => `url(${props.img})`};
`;

function BlogPostCardSmall({ data }) {
  return (
    <CardsContainer className="blog-post__small">
      {data.map((data) => (
        <Card key={data.id} className="card">
          <Image img={data.url} className="card__image"></Image>
          <div className="card__content">
            <div className="title">{data.title}</div>
            <div className="date">{data.date}</div>
            <div className="content">{data.content.slice(0, 300)}...</div>
          </div>
          <div className="btn">wiÄ™cej</div>
        </Card>
      ))}
    </CardsContainer>
  );
}

export default BlogPostCardSmall;
