import React from 'react';
import styled from 'styled-components';

const CardsContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const Card = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;

  .card__content {
    display: flex;
    flex-direction: column;
    /* align-items: center; */
    justify-content: space-around;
    min-height: 100%;
  }
`;

const Image = styled.div`
  background-image: ${(props) => `url(${props.img})`};
`;

function BlogPostCardSmall({ data }) {
  return (
    <CardsContainer className="blog-post__small">
      {data.map((data) => (
        <Card key={data.id} className="card">
          <Image img={data.url} className="card__image"></Image>
          <div className="card__content">
            <div className="title">{data.title}</div>
            <div className="date">{data.date}</div>
            <div className="content">{data.content.slice(0, 300)}...</div>
          </div>
        </Card>
      ))}
    </CardsContainer>
  );
}

export default BlogPostCardSmall;
