import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import ProductGrid from '../components/products/productGrid/ProductGrid';
import { fetchCategoryProducts } from '../redux/singleCategorySlice';
import { polishSigns } from '../components/helpers';
import { colors } from '../components/styles';

const CategoryDiv = styled.div`
  .filters-upper {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px 20px;

    &__icon {
      i {
        font-size: 24px;
        color: rgba(${colors.darkgrey3});
      }
    }
  }
`;

function CategoryPage() {
  const params = useParams();
  const state = useSelector((state) => state.category);
  const dispatch = useDispatch();

  const removePolishSigns = polishSigns(params.name);

  useEffect(() => {
    dispatch(fetchCategoryProducts(removePolishSigns));
  }, []);

  return (
    <CategoryDiv className="category">
      <ProductGrid
        title="kategoria"
        products={state.items.products}
        title={params.name}
      >
        <div className="filters-upper">
          <div className="filters-upper__length">666 produkt√≥w</div>
          <div className="filters-upper__icon">
            <i className="fa fa-filter"></i>
          </div>
        </div>
      </ProductGrid>
    </CategoryDiv>
  );
}

export default CategoryPage;
