import React, { useState } from 'react';
import styled from 'styled-components';
import Footer from '../Footer';

const FilterDiv = styled.div`
  position: absolute;
  width: 100%;
  transform: translateX(50%);

  .filter-item {
    transition: all 0.5s;
    &__name {
    }

    &__content {
      transition: all 0.5s;
      visibility: hidden;
    }
  }
`;

function Filters() {
  const [state, setstate] = useState({ price: false, color: false });
  console.log(state);

  const handleClick = (e) => {
    setstate((prev) => console.log(prev));
  };
  return (
    <FilterDiv>
      <div className="filter-item">
        <div className="filter-item__name" id="price" onClick={handleClick}>
          cena
        </div>
        {state.price ? (
          <div className="filter-item__content">0 zł - 9999 zł</div>
        ) : (
          ''
        )}
      </div>
      <div className="filter-item">
        <div className="filter-item__name" id="color" onClick={handleClick}>
          cena
        </div>
        {state.price ? (
          <div className="filter-item__content">0 zł - 9999 zł</div>
        ) : (
          ''
        )}
      </div>
    </FilterDiv>
  );
}

export default Filters;
